query getCustomerMetafields($customerId: ID!) {
  customer(id: $customerId) {
    id
    firstName
    lastName
    email
    metafields(first: 10) {
      edges {
        node {
          id
          namespace
          key
          value
          type
        }
      }
    }
  }
}

parameters.jsCode:
const cleanData = [];

for (const item of $input.all()) {
  const customer = item.json?.data?.customer;
  if (!customer) continue;

  const rawId = customer.id || "";
  const extractedNumericId = rawId ? rawId.split('/').pop() : null;

  const cleanItem = {
    customerId: rawId || null,
    numericId: extractedNumericId,
    firstName: customer.firstName || "",
    lastName: customer.lastName || "",
    metafields: {},
    hasDiscountOf10: false, // Empezamos asumiendo que es falso
    totalAmount: null // Preparamos un espacio para el monto total
  };

  const edges = customer.metafields?.edges || [];

  for (const edge of edges) {
    const node = edge?.node;

    if (node && node.key && node.value !== null && node.value !== undefined) {

      // Manejamos los descuentos (number_integer)
      if (node.type === "number_integer") {
        const discountValue = parseInt(node.value, 10);
        cleanItem.metafields[node.key] = discountValue;

        // Si encontramos UN descuento que sea exactamente 10, lo marcamos como true
        if (discountValue === 10) {
          cleanItem.hasDiscountOf10 = true;
        }
      } 
      // Manejamos el nuevo campo de dinero (money)
      else if (node.type === "money" && node.key === "monto_total") {
        try {
          const moneyData = JSON.parse(node.value);
          cleanItem.totalAmount = parseFloat(moneyData.amount);
        } catch (e) {
          // Si el JSON falla, no rompemos el código
          cleanItem.totalAmount = null; 
        }
      }
    }
  }

  cleanData.push({ json: cleanItem });
}

return cleanData;

