<body>

  {{ 
    (() => {
      const items = $input.all();
      if (items.length === 0) {
        return `
          <div class="header">
            <h2>¡Hola!</h2>
          </div>
          <div class="no-data">
            <p>No hemos encontrado tu perfil en nuestra base de datos o hubo un error al leer el QR.</p>
          </div>
        `;
      }

      const data = items[0].json;

      // 3. AÑADIDA LA PROPIEDAD "url" A CADA SUCURSAL (Sustituye estos enlaces por los tuyos reales)
      const sucursales = [
        { key: 'sucursal_rules', name: 'Rules Outlet', url: 'https://maps.app.goo.gl/2zcHYJzYi4mku9Bw5' },
        { key: 'sucursal_oakley', name: 'Rules Oakley', url: 'https://maps.app.goo.gl/T81E6eXNTRWc86nj9' },
        { key: 'sucursal_thenorthface', name: 'Rules The North Face', url: 'https://maps.app.goo.gl/T81E6eXNTRWc86nj9' },
        { key: 'sucursal_surfin', name: 'Surfin', url: 'https://maps.app.goo.gl/18GNbRyLLukuo4m57' },
        { key: 'sucursal_nuance', name: 'Nuance', url: 'https://maps.app.goo.gl/Farx9QjYyZX294pe7' },
        { key: 'sucursal_nuancelodge', name: 'Nuance Lodge', url: 'https://maps.app.goo.gl/ND7hyiUQMVSpJ2G96' },
        { key: 'sucursal_melia', name: 'Meliá Sierra Nevada', url: 'https://maps.app.goo.gl/Cf146WRdUBgxN8KT8' }
      ];

      const descuentosActivos = sucursales.filter(s => parseInt(data[s.key] || 0) > 0);

      let html = `
        <div class="header">
          <h2>¡Hola, ${data.Nombre}!</h2>
          <p>Aquí tienes tus descuentos activos en nuestras sucursales:</p>
        </div>
      `;

      if (descuentosActivos.length === 0) {
        html += '<div class="no-data"><p>Actualmente no tienes descuentos activos. ¡Sigue comprando para acumular más!</p></div>';
      } else {
        // AÑADIDA LA CABECERA "Ubicación"
        html += '<table><thead><tr><th>Sucursal</th><th>Descuento</th><th>Ubicación</th></tr></thead><tbody>';

        descuentosActivos.forEach(s => {
          html += `
            <tr>
              <td><strong>${s.name}</strong></td>
              <td><span class="discount-badge">${data[s.key]}%</span></td>
              <td><a href="${s.url}" target="_top" rel="noopener noreferrer" class="map-btn">📍 Ver mapa</a></td>
            </tr>
          `;
        });

        html += '</tbody></table>';
      }

      return html;
    })()
  }}

</body>
</html>

