// Agrupar por empleado
const inputs = items;
const groups = {};
for (const item of inputs) {
  const data = item.json;
  const userId = data.user_id;
  const price = parseFloat(data.total_price) || 0;
  if (!groups[userId]) {
    groups[userId] = { user_id: userId, total_spent: 0, count: 0 };
