// 1. Intentamos recuperar datos de la memoria (si viene del bucle)
const staticData = $getWorkflowStaticData('global');
let allSales = staticData.allProducts;

// 2. Si la memoria est치 vac칤a (porque est치s probando el nodo suelto), usamos el input directo
if (!allSales || allSales.length === 0) {
  allSales = items.map(item => item.json);
}

const productStats = {};

// 3. Recorremos todas las ventas para agruparlas
for (const sale of allSales) {
  // Verificamos que sea una fila v치lida
  if (!sale['NOMBRE DE PRODUCTO']) continue;

  const productName = sale['NOMBRE DE PRODUCTO'];
  const variant = sale['VARIANTE'] || '';
  const key = `${productName} - ${variant}`;

  // Limpieza de n칰meros (por si vienen con comas en vez de puntos)
  let priceStr = (sale['PRECIO PAGADO'] || '0').toString();
  let costStr = (sale['COSTE DE PRODUCTO'] || '0').toString();
  let unitPriceStr = (sale['PRECIO DE PRODUCTO'] || '0').toString(); 

  const price = parseFloat(priceStr.replace(',', '.')) || 0;
  const cost = parseFloat(costStr.replace(',', '.')) || 0;
  const unitPrice = parseFloat(unitPriceStr.replace(',', '.')) || 0;

  // Si es la primera vez que vemos este producto, inicializamos sus contadores
  if (!productStats[key]) {
    productStats[key] = {
      'Producto': productName,
      'Variante': variant,
      'Precio Unidad': unitPrice, // Guardamos el precio original
      'Ventas Totales': 0,        // Dinero real cobrado
      'Unidades Vendidas': 0,     // Cantidad de art칤culos
      'Beneficio': 0
    };
  }

  // Sumamos los valores
  productStats[key]['Ventas Totales'] += price;
  productStats[key]['Unidades Vendidas'] += 1;
  productStats[key]['Beneficio'] += (price - cost);
}

// 4. Ordenamos por Ventas Totales (De mayor a menor)
const sortedProducts = Object.values(productStats).sort((a, b) => 
  b['Ventas Totales'] - a['Ventas Totales']
);

// 5. Cortamos para quedarnos solo con el TOP 10
const top10 = sortedProducts.slice(0, 10);

// 6. Formateamos la salida final y CALCULAMOS EL TOTAL AQU칈
const result = top10.map(p => ({
  json: {
    'Producto': p['Producto'],
    'Variante': p['Variante'],
    'Precio': parseFloat(p['Precio Unidad'].toFixed(2)),
    'Unidades Vendidas': p['Unidades Vendidas'],
    // AQU칈 EST츼 EL TRUCO: Calculamos el total para que no uses f칩rmulas en Excel
    'Total': parseFloat((p['Precio Unidad'] * p['Unidades Vendidas']).toFixed(2)),
    'Beneficio Estimado': parseFloat(p['Beneficio'].toFixed(2))
  }
}));

// 7. Limpiamos la memoria para la pr칩xima vez
staticData.allProducts = [];

return result;

