                Ventas: 0, Unidades: 0, Beneficio: 0
            };
        }
        productStats[prodKey].Ventas += paidPrice;
        productStats[prodKey].Unidades += 1;
        productStats[prodKey].Beneficio += (paidPrice - cost);

