        }
        productStats[prodKey].Ventas += paidPrice;
        productStats[prodKey].Unidades += 1;
        productStats[prodKey].Beneficio += (paidPrice - cost);

        // B) MARCAS
        let detectedBrand = "OTRAS"; // Usamos mayúsculas por consistencia

        for (const marca of LISTA_MARCAS) {
            // Si la marca es un número (ej. "686"), usamos Regex de coincidencia exacta
            if (!isNaN(marca)) {
                const regex = new RegExp(`\\b${marca}\\b`);
                if (regex.test(productName)) {
                    detectedBrand = marca;
                    break;
                }
            } else {
                // Si es texto normal
                if (productName.includes(marca)) {
                    detectedBrand = marca;
                    break;
                }
            }
        }

        // Parche manual para The North Face (buscando en mayúsculas)
        if (detectedBrand === "OTRAS" && (productName.includes("NORTH FACE") || productName.includes(" TNF "))) {
            detectedBrand = "THE NORTH FACE";
        }

        if (!brandStats[detectedBrand]) {
            brandStats[detectedBrand] = { Marca: detectedBrand, Ventas: 0, Unidades: 0 };
        }
        brandStats[detectedBrand].Ventas += paidPrice;
        brandStats[detectedBrand].Unidades += 1;
    }

    // C) SUCURSALES
    if (branchName !== "N/A" && branchName !== "") {
        if (!branchStats[branchName]) {
            branchStats[branchName] = { Sucursal: branchName, Ventas: 0, Transacciones: 0 };
        }
        branchStats[branchName].Ventas += paidPrice;
        branchStats[branchName].Transacciones += 1;
    }
}

// 3. GENERAR RESULTADOS (TOP 10 y demás lógica original)
const finalOutput = [];

// --- PRODUCTOS ---
const sortedProds = Object.values(productStats).sort((a, b) => 
    b.Unidades !== a.Unidades ? b.Unidades - a.Unidades : b.Ventas - a.Ventas
).slice(0, 10);

sortedProds.forEach((p, i) => {
    const item = {
        tipo: "producto",
        fila_excel: i + 2,
        Producto: p.Producto,
        Variante: p.Variante,
        Precio: parseFloat(p.Precio.toFixed(2)),
        Unidades: p.Unidades,
        Total: parseFloat(p.Ventas.toFixed(2)),
        Beneficio: parseFloat(p.Beneficio.toFixed(2))
    };
    if (i === 0) {
        item.Skatepark_Entradas_Vendidas = skateparkCount;
        item.Skatepark_Ingresos_Totales = parseFloat(skateparkRevenue.toFixed(2));
    }
    finalOutput.push({ json: item });
});

// --- MARCAS ---
const sortedBrands = Object.values(brandStats)
    .filter(b => b.Marca !== "OTRAS")
    .sort((a, b) => b.Unidades !== a.Unidades ? b.Unidades - a.Unidades : b.Ventas - a.Ventas)
    .slice(0, 10);

sortedBrands.forEach((b, i) => {
    finalOutput.push({
        json: {
            tipo: "marca",
            fila_excel: i + 14, 
            Marca: b.Marca,
            Unidades: b.Unidades,
            Total: parseFloat(b.Ventas.toFixed(2))
        }
    });
});

// --- SUCURSALES ---
const sortedBranches = Object.values(branchStats).sort((a, b) => b.Ventas - a.Ventas);

sortedBranches.forEach((b, i) => {
    finalOutput.push({
        json: {
            tipo: "sucursal",
            fila_excel: i + FILA_SUCURSALES, 
            Sucursal: b.Sucursal,
            Transacciones: b.Transacciones,
            Total: parseFloat(b.Ventas.toFixed(2))
        }
    });
});

// 4. LIMPIEZA FINAL
staticData.allProducts = [];

return finalOutput;

