// 1. INPUT ACTUAL (items): Son los datos que vienen de "Get row(s)" (Correos)
const datosCorreos = items;

// 2. INPUT ANTERIOR: Recuperamos los datos del nodo "Calcular Ranking Empleados1" (Ventas)
// NOTA: Asegúrate de que el nombre dentro de $('...') sea EXACTO al de tu nodo morado
let datosVentas = [];
try {
  datosVentas = $('Calcular Ranking Empleados').all();
} catch (e) {
  return [{ json: { error: "No encuentro el nodo 'Calcular Ranking Empleados1'. Revisa el nombre." } }];
}

// 3. Creamos el mapa para buscar correos (Nombre -> Correo)
const mapaCorreos = {};

datosCorreos.forEach(fila => {
  const datos = fila.json;

  // En tu captura de "Get row(s)", la columna del nombre se llama "Nombre" (no "Nombre Empleado")
  // Usamos .trim() porque en tu captura se ven saltos de línea ("\n")
  const nombreKey = (datos["Nombre"] || "").toString().trim();
  const email = (datos["Correo"] || "").toString().trim(); // Limpiamos el \n del correo

  if (nombreKey && email) {
    mapaCorreos[nombreKey] = email;
  }
});

// 4. Procesamos las VENTAS (iteramos sobre datosVentas, NO sobre items)
const resultados = [];

for (const item of datosVentas) {
  const empleado = item.json;

  // Aquí sí existen estas columnas porque estamos leyendo del nodo de Ranking
  const ventas = parseFloat(empleado["Total Vendido"]);
  const nombreVentas = (empleado["Nombre Empleado"] || "").toString().trim();

  let bonoValor = 0;

  // --- LÓGICA DEL BONO ---
  if (ventas >= 30000) {
    bonoValor = 200;
  } else if (ventas >= 26000) {
    bonoValor = 150;
  } else if (ventas >= 22000) {
    bonoValor = 120;
  } else if (ventas >= 18000) {
    bonoValor = 90;
  }

  // --- GENERACIÓN DEL OUTPUT ---
  if (bonoValor > 0) {

    // Buscamos el correo usando el nombre limpio
    const correoEncontrado = mapaCorreos[nombreVentas];

    resultados.push({
      json: {
        "Nombre Empleado": nombreVentas,
        "Total Vendido": ventas,
        "BONO": `BONO ${bonoValor}€`,
        "MontoBono": bonoValor,
        // Si no encuentra el correo, pondrá null.
        "Correo": correoEncontrado || "No encontrado"
      }
    });
  }
}

return resultados;

// 1. Obtenemos los resultados de Shopify (Input actual)
const shopifyInput = items;

// 2. Obtenemos los datos originales del bono (Code in JavaScript)
let bonusInput = [];
try {
  bonusInput = $('Code in JavaScript').all();
} catch (error) {
  return [{ json: { error: "No se encuentran datos del nodo 'Code in JavaScript'" } }];
}

// 3. Mapeamos los bonos por correo (limpiando espacios)
const mapaBonos = {};
bonusInput.forEach(item => {
  const data = item.json;
  if (data.Correo) {
    const emailLimpio = data.Correo.toString().trim().toLowerCase();
    mapaBonos[emailLimpio] = data;
  }
});

const resultadosFinales = [];

// 4. Procesamos la respuesta de Shopify
for (const item of shopifyInput) {
  const shopifyData = item.json;

  // Verificamos si Shopify encontró al cliente
  if (shopifyData.data && 
      shopifyData.data.customers && 
      shopifyData.data.customers.edges && 
      shopifyData.data.customers.edges.length > 0) {

    const clienteNode = shopifyData.data.customers.edges[0].node;
    const emailShopify = clienteNode.email.trim().toLowerCase();

    // Cruzamos con el bono original
    const bonoOriginal = mapaBonos[emailShopify];

    if (bonoOriginal) {

      // Intentamos obtener la ID de la cuenta de crédito
      let creditAccountId = null;
      if (clienteNode.storeCreditAccounts && 
          clienteNode.storeCreditAccounts.edges && 
          clienteNode.storeCreditAccounts.edges.length > 0) {
        creditAccountId = clienteNode.storeCreditAccounts.edges[0].node.id;
      }

      // IMPORTANTE: Ahora devolvemos el resultado SIEMPRE, tenga o no cuenta de crédito.
      // Si no tiene cuenta, 'CreditAccountID' será null.
      resultadosFinales.push({
        json: {
          "CustomerID": clienteNode.id,
          "CreditAccountID": creditAccountId, // Puede ser null si el cliente es nuevo
          "MontoBono": bonoOriginal.MontoBono.toString(),
          "EtiquetaBono": bonoOriginal.BONO,
          "Email": emailShopify,
          "TieneCuentaCredito": creditAccountId ? true : false // Bandera de ayuda
        }
      });
    }
  }
}

return resultadosFinales;

