const brandStats = {};
const branchStats = {};

// Configuración
let skateparkCount = 0;
let skateparkRevenue = 0;
const SKATEPARK_NAME = "TICKET ENTRADA SKATEPARK";
const FILA_SUCURSALES = 26; 

// 2. PROCESAMIENTO ÚNICO
for (const sale of allSales) {
    const productName = sale['NOMBRE DE PRODUCTO'];
    const branchName = sale['SUCURSAL DE VENTA'];

    // Limpieza números
    let priceStr = (sale['PRECIO PAGADO'] || '0').toString();
    let costStr = (sale['COSTE DE PRODUCTO'] || '0').toString();
    let unitPriceStr = (sale['PRECIO DE PRODUCTO'] || '0').toString();

    const paidPrice = parseFloat(priceStr.replace(',', '.')) || 0;
    const cost = parseFloat(costStr.replace(',', '.')) || 0;
    const listPrice = parseFloat(unitPriceStr.replace(',', '.')) || 0;

    // Lógica Skatepark
    if (productName && productName.includes(SKATEPARK_NAME)) {
        skateparkCount += 1;
        skateparkRevenue += paidPrice;
        continue; 
    }

    if (productName) {
        // A) PRODUCTOS
        const variant = sale['VARIANTE'] || '';
        const prodKey = `${productName} - ${variant}`;

        if (!productStats[prodKey]) {
            productStats[prodKey] = {
                Producto: productName, Variante: variant, Precio: listPrice,
                Ventas: 0, Unidades: 0, Beneficio: 0
            };
        }
        productStats[prodKey].Ventas += paidPrice;
        productStats[prodKey].Unidades += 1;
        productStats[prodKey].Beneficio += (paidPrice - cost);

        // B) MARCAS
        let detectedBrand = "Otras";
        for (const marca of LISTA_MARCAS) {
            if (marca && productName.toLowerCase().includes(marca.toLowerCase())) {
                detectedBrand = marca;
                break;
            }
        }
        if (!brandStats[detectedBrand]) {
            brandStats[detectedBrand] = { Marca: detectedBrand, Ventas: 0, Unidades: 0 };
        }
        brandStats[detectedBrand].Ventas += paidPrice;
        brandStats[detectedBrand].Unidades += 1;
    }

    // C) SUCURSALES
    if (branchName && branchName !== "N/A") {
        if (!branchStats[branchName]) {
            branchStats[branchName] = { Sucursal: branchName, Ventas: 0, Transacciones: 0 };
        }
        branchStats[branchName].Ventas += paidPrice;
        branchStats[branchName].Transacciones += 1;
    }
}

// 3. GENERAR RESULTADOS
const finalOutput = [];

// --- PRODUCTOS (Top 10) ---
const sortedProds = Object.values(productStats).sort((a, b) => 
    b.Unidades !== a.Unidades ? b.Unidades - a.Unidades : b.Ventas - a.Ventas
).slice(0, 10);

sortedProds.forEach((p, i) => {
    const item = {
        tipo: "producto",
        fila_excel: i + 2,
        Producto: p.Producto,
        Variante: p.Variante,
        Precio: parseFloat(p.Precio.toFixed(2)),
        Unidades: p.Unidades,
        Total: parseFloat(p.Ventas.toFixed(2)),
        Beneficio: parseFloat(p.Beneficio.toFixed(2))
    };
    if (i === 0) {
        item.Skatepark_Entradas_Vendidas = skateparkCount;
        item.Skatepark_Ingresos_Totales = parseFloat(skateparkRevenue.toFixed(2));
    }
    finalOutput.push({ json: item });
});

