parameters.query:
query getOrderData($id: ID!) {
  order(id: $id) {
    customer {
      id
    }
    discountCodes
  }
}

parameters.jsCode:
// 1. Obtener datos del Webhook
// IMPORTANTE: Aquí debe ir el nombre EXACTO de tu nodo.
// En tu captura se llama "Webhook2". Si lo cambias, actualiza esta línea.
let webhookNode;
try {
    webhookNode = $('Webhook2').first().json; 
} catch (error) {
    // Fallback: Si no se llama Webhook2, intentamos buscar el nodo anterior conectado
    // Esto hace el código más resistente a cambios de nombre
    webhookNode = $input.all()[0].json;
}

const topic = webhookNode.headers["x-shopify-topic"];
const body = webhookNode.body;

let customerId = "";
let amountToProcess = 0;
let discountCode = null;

if (topic.includes('orders/create')) {
    // === ES UN PEDIDO ===
    if (body.customer && body.customer.admin_graphql_api_id) {
        customerId = body.customer.admin_graphql_api_id;
    }

    // --- DETECCIÓN DE CÓDIGO/NOMBRE DE DESCUENTO ---

    // PRIORIDAD 1: Códigos de Descuento (Cupones estándar)
    if (body.discount_codes && body.discount_codes.length > 0) {
        discountCode = body.discount_codes[0].code;
    }
    // PRIORIDAD 2: Aplicaciones de Descuento (Manuales POS, Automáticos, Scripts)
    else if (body.discount_applications && body.discount_applications.length > 0) {
        discountCode = body.discount_applications[0].title;
    }

    // --- DETECCIÓN DE MONTO ---
    const totalDiscounts = parseFloat(body.total_discounts || "0");

    // Solo acumulamos si hubo un descuento real
    if (totalDiscounts > 0) {
        amountToProcess = parseFloat(body.total_price);
    } else {
        amountToProcess = 0;
    }

} else if (topic.includes('refunds/create')) {
    // === ES UNA DEVOLUCIÓN ===
    // En las devoluciones, los datos vienen del nodo GraphQL anterior, no del webhook directo
    // Usamos try/catch por si se ejecuta manualmente sin datos previos
    let orderData = null;
    try {
        orderData = $input.item.json.data ? $input.item.json.data.order : null;
    } catch(e) {}

    if (orderData && orderData.customer) {
         customerId = orderData.customer.id;

         if (orderData.discountCodes && orderData.discountCodes.length > 0) {
             const codeItem = orderData.discountCodes[0];
             discountCode = (typeof codeItem === 'string') ? codeItem : codeItem.code;
         } 
    } else {
         customerId = body.order_id; 
    }

    const transactions = body.transactions || [];
    const totalRefunded = transactions.reduce((acc, curr) => acc + parseFloat(curr.amount), 0);

    amountToProcess = -Math.abs(totalRefunded);
}

return {
    json: {
        customerId: customerId,
        amountToProcess: amountToProcess,
        discountCode: discountCode
    }
};

parameters.query:
query getCustomerMetafields($customerId: ID!) {
  customer(id: $customerId) {
    id
    total: metafield(namespace: "custom", key: "monto_total") {
      value
    }
    profesor: metafield(namespace: "custom", key: "monto_profesor_40") {
      value
    }
    club: metafield(namespace: "custom", key: "monto_club_30") {
      value
    }
    empleado: metafield(namespace: "custom", key: "monto_empleado_25") {
      value
    }
  }
}

parameters.jsCode:
// === CONFIGURACIÓN ===
const codeMap = {
    "40%PROFESOR": "monto_profesor_40",
    "30%CLUB": "monto_club_30",
    "25%EMPLEADO": "monto_empleado_25"
};

const rules = [
  { threshold: 600, tagToRemove: "DescuentoEmpleado" },
  { threshold: 1000, tagToRemove: "DescuentoClub" },
  { threshold: 1200, tagToRemove: "DescuentoProfesor" }
];

// === DATOS DE ENTRADA ===
const processData = $('Code Unificador1').first().json; 
const amount = processData.amountToProcess;
const codeUsed = processData.discountCode;

const customerData = $input.item.json.data.customer;
const customerId = customerData.id;

const getVal = (metafieldObj) => {
    if (!metafieldObj || !metafieldObj.value) return 0;
    try {
        const parsed = JSON.parse(metafieldObj.value);
        return parseFloat(parsed.amount);
    } catch (e) {
        return 0;
    }
};

// === 1. CALCULAR MONTO TOTAL ===
// (Acumula cualquier pedido con descuento, sea código o personalizado)
let currentTotal = getVal(customerData.total);
let newTotal = currentTotal + amount;
if (newTotal < 0) newTotal = 0;

let metafieldsToUpdate = [];

// Actualizar TOTAL
metafieldsToUpdate.push({
    ownerId: customerId,
    namespace: "custom",
    key: "monto_total",
    type: "money",
    value: JSON.stringify({ amount: newTotal.toFixed(2), currency_code: "EUR" })
});

// === 2. CALCULAR MONTO ESPECÍFICO ===
// (Solo si coincide el código string exacto)
if (codeUsed && codeMap[codeUsed]) {
    const specificKey = codeMap[codeUsed];
    let currentSpecific = 0;

    if (codeUsed === "40%PROFESOR") currentSpecific = getVal(customerData.profesor);
    else if (codeUsed === "30%CLUB") currentSpecific = getVal(customerData.club);
    else if (codeUsed === "25%EMPLEADO") currentSpecific = getVal(customerData.empleado);

    let newSpecific = currentSpecific + amount;
    if (newSpecific < 0) newSpecific = 0;

    metafieldsToUpdate.push({
        ownerId: customerId,
        namespace: "custom",
        key: specificKey,
        type: "money",
        value: JSON.stringify({ amount: newSpecific.toFixed(2), currency_code: "EUR" })
    });
}

// === 3. GESTIÓN DE ETIQUETAS ===
let tagsToRemove = [];
for (const rule of rules) {
    if (newTotal >= rule.threshold) {
        tagsToRemove.push(rule.tagToRemove);
    }
}

return {
    json: {
        customerId: customerId,
        metafieldsToUpdate: metafieldsToUpdate,
        tagsToRemove: tagsToRemove,
        shouldRemoveTags: tagsToRemove.length > 0
    }
};

parameters.query:
mutation updateMetafields($metafields: [MetafieldsSetInput!]!) {
  metafieldsSet(metafields: $metafields) {
    metafields {
      key
      value
    }
    userErrors {
      field
      message
    }
  }
}

mutation removeCustomerTags($id: ID!, $tags: [String!]!) {
  tagsRemove(id: $id, tags: $tags) {
    userErrors {
      field
      message
    }
    node {
      id
    }
  }
}

