query getOrder($id: ID!) {

  order(id: $id) {

    id

    name

    createdAt

    email

    totalPriceSet {

      shopMoney {

        amount

        currencyCode

      }

    }

    lineItems(first: 10) {

      edges {

        node {

          title

          quantity

          originalUnitPriceSet {

            shopMoney {

              amount

              currencyCode

            }

          }

        }

      }

    }

    fulfillments {

      id

      location {

        name

        address {

          address1

          city

          province

          country

          zip

        }

      }

    }

    transactions(first: 5) {

      formattedGateway

      kind

      status

    }

    customer {

      firstName

      lastName

      email

    }

  }

}

