    // IDs útiles para Shopify
    refund_item_id: item.id,
    product_id: item.line_item.product_id,
    variant_id: item.line_item.variant_id,

    // Contexto general del pedido (muy útil para los siguientes nodos)
    order_id: inputData.body.order_id,
    main_refund_id: inputData.body.id
  };

  // 5. Lo metemos en el array con el formato de n8n
  results.push({ json: productData });
}

