query getOrder($id: ID!) {
  order(id: $id) {
    id
    name
    createdAt
    email
    totalPriceSet {
      shopMoney {
        amount
        currencyCode
      }
    }
    lineItems(first: 10) {
      edges {
        node {
          title
          quantity
          # NUEVO: Si esto es menor que 'quantity', significa que se ha devuelto
          refundableQuantity 
          originalUnitPriceSet {
            shopMoney {
              amount
              currencyCode
            }
          }
        }
      }
    }
    # NUEVO: Sección completa con el historial de devoluciones
    refunds(first: 5) {
      id
      createdAt
      note
      totalRefundedSet {
        shopMoney {
          amount
          currencyCode
        }
      }
      refundLineItems(first: 10) {
        edges {
          node {
            quantity
            lineItem {
              title
            }
          }
        }
      }
    }
    fulfillments {
      id
      location {
        name
        address {
          address1
          city
          province
          country
          zip
        }
      }
    }
    transactions(first: 5) {
      formattedGateway
      kind
      status
    }
    customer {
      firstName
      lastName
      email
    }
  }
}
