// 1. Obtenemos el HTML gigante
const html = items[0].json.data;

// 2. Buscamos el script que tiene todos los datos de la página (Next.js data)
const regex = /<script id="__NEXT_DATA__" type="application\/json">(.*?)<\/script>/;
const match = html.match(regex);

let resultados = [];

if (match && match[1]) {
  // 3. Parseamos el texto a JSON real
  const data = JSON.parse(match[1]);

  // 4. Navegamos por la estructura compleja de Sierra Nevada para encontrar las webcams
  // La ruta suele ser: props -> pageProps -> model -> MenuPrincipalCustom... o buscando bloques
  // Para hacerlo fácil, vamos a buscar recursivamente cualquier objeto que parezca una webcam de Feratel

  // Convertimos todo el objeto a string para buscar patrones de feratel (truco rápido)
  const jsonString = JSON.stringify(data);

  // Buscamos patrones de IDs de cámara (ej: cam=15111)
  const camRegex = /cam=(\d+)/g;
  let camMatch;
  const uniqueCams = new Set();

  while ((camMatch = camRegex.exec(jsonString)) !== null) {
    uniqueCams.add(camMatch[1]);
  }

  // Creamos el resultado
  uniqueCams.forEach(id => {
    resultados.push({
      json: {
        camera_id: id,
        iframe_url: `https://webtv.feratel.com/webtv/?design=v5&cam=${id}`,
        video_directo_mp4: `https://sts063.feratel.co.at/streams/stsstore055/1/${id}_vid.mp4` // URL estimada basada en tu ejemplo
      }
    });
  });
}

return resultados;

// Obtenemos la fecha actual
const now = new Date();
let masCercano = null;
let menorDiferencia = Infinity; 

for (const item of items) {
  // Asegúrate de que item.json existe por seguridad
  if (!item.json) continue;

  const url = item.json.video_url;

  // --- BLOQUE DE SEGURIDAD NUEVO ---
  // Si no hay url, o no es un texto, saltamos al siguiente item del bucle
  if (!url || typeof url !== 'string') {
    continue; 
  }
  // ---------------------------------

  // Ahora es seguro usar .match()
  const match = url.match(/_([0-9a-fA-F]{8})[-_a-zA-Z]/);

  if (match && match[1]) {
    const hexTimestamp = match[1];
    const unixSeconds = parseInt(hexTimestamp, 16);

    const videoDate = new Date(unixSeconds * 1000);
    const diff = now - videoDate;

    if (diff >= 0 && diff < menorDiferencia) {
      menorDiferencia = diff;
      masCercano = item;
      masCercano.json.fecha_decodificada = videoDate.toISOString();
      masCercano.json.segundos_atras = diff / 1000;
    }
  }
}

return masCercano ? [masCercano] : [];

