parameters.query:
query findCustomer($busqueda_shopify: String!) {
  customers(first: 1, query: $busqueda_shopify) {
    edges {
      node {
        id
        tags
        email
        phone
      }
    }
  }
}

mutation customerUpdate($input: CustomerInput!) {
  customerUpdate(input: $input) {
    customer {
      id
      email
      firstName
      lastName
      tags
      phone
    }
    userErrors {
      field
      message
    }
  }
}

