// Acceso seguro a la raíz de los datos
const item = items[0].json;

// Verificación inicial
if (!item.Partes || !item.Partes[0] || !item.Partes[0].Parte) {
  return [{ json: { error: "Estructura Partes/Parte no encontrada" } }];
}

const root = item.Partes[0].Parte.partenieve;
const nieve = root.nieve;
// Para la meteo seguimos usando la pagina[0] porque ahí suelen estar los datos generales
const meteo = root.meteorologia.paginas.pagina[0];

// ----------------------------------------------------
// LÓGICA DE ZONAS ABIERTAS (Iterando todas las páginas)
// ----------------------------------------------------
let zonasAbiertasArr = [];
let debugLog = []; 

// 1. Obtenemos el array de páginas de pistas
// root.pistas.paginas.pagina puede ser un array de objetos o un solo objeto
let paginasPistas = root.pistas.paginas.pagina;
if (!Array.isArray(paginasPistas)) {
  paginasPistas = [paginasPistas];
}

// 2. Iteramos por cada PÁGINA (0, 1, 2, 3...)
for (const pagina of paginasPistas) {

  // Dentro de cada página, buscamos las ZONAS
  let zonasEnPagina = pagina.zona;

  // Aseguramos que sea array (por si en una página solo hay 1 zona)
  if (!Array.isArray(zonasEnPagina)) {
    if (!zonasEnPagina) continue; // Si la página está vacía, saltamos
    zonasEnPagina = [zonasEnPagina];
  }

  // 3. Iteramos por cada ZONA de esa página
  for (const zona of zonasEnPagina) {
    const nombreZona = zona["@nombre"];
    let zonaAbierta = false;

    // Obtenemos las claves (las pistas dentro de la zona)
    const claves = Object.keys(zona);

    for (const clave of claves) {
      if (clave === "@nombre") continue; // Saltamos el nombre

      const pista = zona[clave];

      // Verificamos que sea un objeto de pista
      if (pista && typeof pista === 'object') {

        // Buscamos el estado en @estado (pista normal) o #text (subpista)
        let estadoRaw = pista["@estado"] || pista["#text"];

        if (estadoRaw) {
          const estado = String(estadoRaw).trim().toLowerCase();

          // Si encontramos "true" o "parcial", la zona cuenta como abierta
          if (estado === "true" || estado === "parcial") {
            zonaAbierta = true;
            // debugLog.push(`Abierta: ${nombreZona} (Pista: ${clave})`); // Descomentar para depurar
            break; // Dejamos de mirar esta zona, ya sabemos que está abierta
          }
        }
      }
    }

    // Si la zona tiene algo abierto y NO la hemos añadido ya a la lista, la agregamos
    if (zonaAbierta && !zonasAbiertasArr.includes(nombreZona)) {
      zonasAbiertasArr.push(nombreZona);
    }
  }
}

// ----------------------------------------------------
// RESULTADO FINAL
// ----------------------------------------------------
const resultado = {
  // Lista de zonas separadas por coma
  "zonas_abiertas": zonasAbiertasArr.join(", "),
  "numerozonas": root.pistas["@totalpistasabiertas"],

  // Datos Nieve (limpiando espacios con trim)
  "calidadveleta": nieve["calidadveleta"]?.trim(),
  "calidadborreguiles": nieve["calidadborreguiles"]?.trim(),
  "calidadlomadilar": nieve["calidadlomadilar"]?.trim(),
  "calidadlaguna": nieve["calidadlaguna"]?.trim(),
  "calidadrio": nieve["calidadrio"]?.trim(),
  "calidadparador": nieve["calidadparador"]?.trim(),
  "calidadnieve_general": nieve["calidadnieve"]?.trim(),

  // Datos Meteo (convertidos a enteros)
  "temperaturaveleta": parseInt(meteo["temperaturaveleta"]),
  "temperaturaborreguiles": parseInt(meteo["temperaturaborreguiles"]),
  "temperaturapradollano": parseInt(meteo["temperaturapradollano"]),

  "vientoveleta": parseInt(meteo["vientoveleta"]),
  "vientoborreguiles": parseInt(meteo["vientoborreguiles"]),
  "vientopradollano": parseInt(meteo["vientopradollano"]),

  "estadocieloveleta": meteo["estadocieloveleta"]?.trim(),
  "estadocieloborreguiles": meteo["estadocieloborreguiles"]?.trim(),
  "estadocielopradollano": meteo["estadocielopradollano"]?.trim(),

  "visibilidadveleta": meteo["visibilidadveleta"]?.trim(),
  "visibilidadborreguiles": meteo["visibilidadborreguiles"]?.trim(),
  "visibilidadpradollano": meteo["visibilidadpradollano"]?.trim(),

  // Generales
  "espesorminimo": nieve["espesorminimo"],
  "espesormaximo": nieve["espesormaximo"],
  "km_esquiables": root.pistas["@kilometrosesquiables"],
  "remontes_abiertos": root.remontes["@totalremontesabiertos"],
  "fecha": root.general["fecha"]
};

return [{ json: resultado }];

const data = $input.first().json;

// Función auxiliar para calcular la moda (el valor más repetido)
function calcularModa(valores) {
    const mapa = {};
    let moda = '';
    let maxCount = 0;

    for (const val of valores) {
        // Normalizamos a mayúsculas
        const cleanedVal = val.toUpperCase(); 

        mapa[cleanedVal] = (mapa[cleanedVal] || 0) + 1;

        if (mapa[cleanedVal] > maxCount) {
            maxCount = mapa[cleanedVal];
            moda = cleanedVal;
        }
    }
    return moda;
}

// 1. CÁLCULO DE LA MODA DEL ESTADO DEL CIELO
const estadosCielo = [
    data.estadocieloveleta,
    data.estadocieloborreguiles,
    data.estadocielopradollano
];
const modaEstadoCielo = calcularModa(estadosCielo);


// 2. CÁLCULO DE LA MODA DE LA VISIBILIDAD (Nuevo cálculo solicitado)
const visibilidades = [
    data.visibilidadveleta,
    data.visibilidadborreguiles,
    data.visibilidadpradollano
];
const modaVisibilidad = calcularModa(visibilidades);


// 3. RETORNO DE LOS DATOS CON EL FORMATO ESPECÍFICO
return [{
    json: {
        // Output solicitado: Moda de los estados del cielo
        estadogeneral: modaEstadoCielo,

        // Output solicitado: Moda de la visibilidad
        visibilidadgeneral: modaVisibilidad
    }
}];

const item = items[0].json;

return [{
  json: {
    "zonas_abiertas": item.zonas_abiertas,
    "numerozonas": item.numerozonas
  }
}];

// Obtiene el primer (y único) ítem del array de entrada
const item = items[0].json;

// Inicializa las variables para la temperatura y el viento
let tempMin = Infinity;
let tempMax = -Infinity;
let vientoMin = Infinity;
let vientoMax = -Infinity;

// Objeto que mapea las claves de calidad de nieve a sus claves de temperatura y viento correspondientes
// Usamos un array si la temperatura/viento de una zona abierta debe tomarse de un punto de medición diferente
const mapaZonas = {
    // Zona: [Clave de Calidad, Clave de Temperatura, Clave de Viento]
    "borreguiles": ["calidadborreguiles", "temperaturaborreguiles", "vientoborreguiles"],
    "veleta": ["calidadveleta", "temperaturaveleta", "vientoveleta"],
    "rio": ["calidadrio", "temperaturaborreguiles", "vientoborreguiles"], // Asumimos temp/viento del Río se toman de Borreguiles
    "lomadilar": ["calidadlomadilar", null, null], // No tienen datos de temp/viento en tu JSON de ejemplo
    "laguna": ["calidadlaguna", null, null],
    "parador": ["calidadparador", null, null]
};

// **1. Iterar sobre las zonas y procesar las abiertas**
for (const zona in mapaZonas) {

    const [calidadKey, tempKey, vientoKey] = mapaZonas[zona];

    // 1.1. Verificar si la zona está ABIERTA
    if (item.hasOwnProperty(calidadKey) && item[calidadKey] !== "CERRADA" && item[calidadKey] !== "cerrada") {

        // 1.2. Calcular Temperaturas
        if (tempKey && item.hasOwnProperty(tempKey)) {
            const temperatura = item[tempKey];
            if (typeof temperatura === 'number') {
                if (temperatura < tempMin) { tempMin = temperatura; }
                if (temperatura > tempMax) { tempMax = temperatura; }
            }
        }

        // 1.3. Calcular Viento
        if (vientoKey && item.hasOwnProperty(vientoKey)) {
            const viento = item[vientoKey];
            if (typeof viento === 'number') {
                if (viento < vientoMin) { vientoMin = viento; }
                if (viento > vientoMax) { vientoMax = viento; }
            }
        }
    }
}

// **2. Incluir la temperatura y viento de Pradollano (Base)**
// La base se incluye siempre que los datos existan.
const tempPradollano = item.temperaturapradollano;
if (typeof tempPradollano === 'number') {
    if (tempPradollano < tempMin) { tempMin = tempPradollano; }
    if (tempPradollano > tempMax) { tempMax = tempPradollano; }
}

const vientoPradollano = item.vientopradollano;
if (typeof vientoPradollano === 'number') {
    if (vientoPradollano < vientoMin) { vientoMin = vientoPradollano; }
    if (vientoPradollano > vientoMax) { vientoMax = vientoPradollano; }
}


// **3. Crear el nuevo objeto de resultado**

// Ajustar valores si no se encontraron datos válidos
if (tempMin === Infinity) { tempMin = null; }
if (tempMax === -Infinity) { tempMax = null; }
if (vientoMin === Infinity) { vientoMin = null; }
if (vientoMax === -Infinity) { vientoMax = null; }

// El nodo de n8n solo devolverá el objeto resultante con las cuatro variables
const output = {
    temperaturaminima: tempMin,
    temperaturamaxima: tempMax,
    vientominimo: vientoMin,
    vientomaximo: vientoMax
};

// Se retorna el resultado dentro del formato de array que espera n8n
return [
    { json: output }
];

// $input.all() toma el array completo de ítems que salió del Merge Node.
const inputItems = $input.all(); 
const mergedData = {};

// Iteramos sobre los 4 ítems separados
for (const item of inputItems) {
    // El método Object.assign() fusiona todas las propiedades (claves y valores) 
    // del item actual (item.json) en el objeto final (mergedData).
    Object.assign(mergedData, item.json);
}

// Devolvemos el array con el único ítem fusionado.
return [{
    json: mergedData
}];

// Obtiene el único ítem de entrada que contiene todos los datos fusionados
const item = $input.first();

// La fecha está en formato DD/MM/AAAA (ej: "02/12/2025")
const fechaString = item.json.fecha;

// --- 1. PREPARAR LA CADENA PARA CONVERTIR A OBJETO DATE ---
// JavaScript prefiere el formato AAAA-MM-DD para crear fechas.
// Invertimos la cadena de fecha de DD/MM/AAAA a AAAA-MM-DD.
const partes = fechaString.split('/');
// Asegúrate de que las partes existen antes de usar sus índices
if (partes.length === 3) {
    // La nueva cadena será AAAA-MM-DD
    const fechaISO = `${partes[2]}-${partes[1]}-${partes[0]}`;

    // --- 2. CREAR EL OBJETO DATE ---
    // Creamos el objeto Date (restamos 1 al mes porque JS los cuenta de 0 a 11)
    const fechaObj = new Date(fechaISO); 

    // --- 3. FORMATEAR AL ESPAÑOL ---
    // Usamos Intl.DateTimeFormat para el formato completo y en español
    const opciones = { 
        weekday: 'long', 
        year: 'numeric', 
        month: '2-digit', 
        day: '2-digit' 
    };

    // Formato de salida: "Martes, 02/12/2025"
    const fechaFormateadaCompleta = fechaObj.toLocaleDateString('es-ES', opciones);

    // Ajustamos el formato para que sea exactamente "DíaSemana dd/mm/aaaa" (sin la coma)
    const [diaSemana, fechaSimple] = fechaFormateadaCompleta.split(', ');
    const fechaFinal = `${diaSemana} ${fechaSimple}`;

    // --- 4. ACTUALIZAR EL CAMPO EN EL JSON ---
    item.json.fecha = fechaFinal;

} else {
    // Si la cadena de fecha no tiene el formato esperado, la dejamos como está.
    item.json.fecha = "Formato de fecha inválido"; 
}

// Devolvemos el ítem modificado
return [item];

// Zonas que tú quieres conservar
const zonasDeseadas = [
  'Veleta',
  'Borreguiles',
  'Parque Sulayr',
  'Loma Dilar',
  'Pista del Mar. (Zona Familiar)',
  'Rio Monachil',
  'Jardin de Nieve'
];

return items.map(item => {
  // Convertimos el string en array
  const zonas = item.json.zonas_abiertas
    .split(',')
    .map(z => z.trim());

  // Filtramos solo las deseadas
  const zonasFiltradas = zonas.filter(z =>
    zonasDeseadas.some(zd => z.includes(zd))
  );

  return {
    json: {
      ...item.json,
      zonas_abiertas: zonasFiltradas
    }
  };
});

// Iteramos sobre todos los elementos que entran al nodo
return $input.all().map(item => {
  // Obtenemos el valor actual, asegurando que existe y pasándolo a mayúsculas
  const estadoOriginal = (item.json.estadogeneral || "").toUpperCase();

  let estadoFinal = "NUBOSO"; // Valor por defecto (fallback)

  // Lógica de mapeo
  if (estadoOriginal.includes("NEVANDO")) {
    estadoFinal = "NEVANDO";
  } 
  else if (estadoOriginal === "DESPEJADO") {
    estadoFinal = "DESPEJADO";
  } 
  else if (estadoOriginal === "POCO NUBOSO") {
    // Asignamos 'SOLEADO' a 'POCO NUBOSO' ya que es la mejor coincidencia
    estadoFinal = "SOLEADO";
  } 
  else {
    // Aquí caerán: "NUBOSO", "MUY NUBOSO", "CUBIERTO", 
    // "CUBIERTO PARCIAL" y "NUBOSO PARCIAL"
    estadoFinal = "NUBOSO";
  }

  // Escribimos el resultado en el JSON. 
  // Nota: He creado un campo nuevo 'estado_simple' para no borrar el original,
  // pero puedes cambiarlo a 'item.json.estadogeneral' si prefieres sobreescribirlo.
  item.json.estado_simple = estadoFinal;

  return item;
});
