            color: #e0e0e0;
            margin: 0;
            padding: 20px;
            display: flex;
            justify-content: center;
        }
        .form-container {
            background-color: #1e1e1e;
            padding: 30px;
            border-radius: 10px;
            box-shadow: 0 4px 15px rgba(0, 0, 0, 0.5);
            max-width: 650px;
            width: 100%;
        }
        h2 {
            color: #ffffff;
            text-align: center;
            margin-top: 0;
            margin-bottom: 25px;
        }
        .form-group {
            margin-bottom: 15px;
        }
        .form-row {
            display: flex;
            gap: 15px;
            margin-bottom: 15px;
        }
        .form-row .form-group {
            flex: 1;
            margin-bottom: 0;
        }
        label {
            display: block;
            font-weight: bold;
            margin-bottom: 5px;
            color: #b0bec5;
            font-size: 13px;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }
        input[type="text"],
        select {
            width: 100%;
            padding: 10px;
            background-color: #2c2c2c;
            color: #ffffff;
            border: 1px solid #555555;
            border-radius: 5px;
            box-sizing: border-box;
            font-size: 15px;
            transition: border-color 0.3s, box-shadow 0.3s;
        }
        input[type="text"]::placeholder {
            color: #757575;
        }
        input[type="text"]:focus,
        select:focus {
            outline: none;
            border-color: #4fc3f7;
            box-shadow: 0 0 0 3px rgba(79, 195, 247, 0.2);
        }
        /* Color específico para las opciones del select en algunos navegadores */
        select option {
            background-color: #2c2c2c;
            color: #ffffff;
        }
        .input-with-unit {
            display: flex;
            align-items: center;
        }
        .input-with-unit input {
            border-top-right-radius: 0;
            border-bottom-right-radius: 0;
        }
        .unit {
            background-color: #333333;
            border: 1px solid #555555;
            border-left: none;
            padding: 10px;
            border-top-right-radius: 5px;
            border-bottom-right-radius: 5px;
            color: #b0bec5;
            font-size: 14px;
        }

        /* Estilos específicos para los Checkboxes */
        .checkbox-group {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
            gap: 10px;
            margin-top: 8px;
        }
        .checkbox-label {
            display: flex;
            align-items: center;
            font-weight: normal;
            text-transform: none;
            letter-spacing: normal;
            color: #e0e0e0;
            font-size: 14px;
            cursor: pointer;
            background-color: #2c2c2c;
            padding: 10px 12px;
            border-radius: 5px;
            border: 1px solid #555555;
            transition: background-color 0.2s, border-color 0.2s;
            margin-bottom: 0;
        }
        .checkbox-label:hover {
            background-color: #383838;
            border-color: #4fc3f7;
        }
        .checkbox-label input[type="checkbox"] {
            margin-right: 10px;
            accent-color: #0288d1;
            width: 18px;
            height: 18px;
            cursor: pointer;
        }

        button {
            width: 100%;
            background-color: #0288d1;
            color: white;
            padding: 14px;
            border: none;
            border-radius: 5px;
            font-size: 16px;
            font-weight: bold;
            cursor: pointer;
            margin-top: 25px;
            transition: background-color 0.3s;
        }
