                flex-direction: column;
                gap: 15px;
            }
            .checkbox-group {
                grid-template-columns: 1fr 1fr;
            }
        }
    </style>
</head>
<body>

    <div class="form-container">
        <h2>❄️ Actualizar Parte de Nieve</h2>

        <form action="https://n8n.tailwind.es/webhook/recibirFormulario" method="POST">

            <div class="form-group">
                <label for="calidad_nieve">Calidad Nieve</label>
                <input type="text" id="calidad_nieve" name="calidad_nieve" required placeholder="Ej. Polvo, Dura, Primavera...">
            </div>

            <div class="form-group">
                <label for="aspecto_cielo">Aspecto del Cielo</label>
                <select id="aspecto_cielo" name="aspecto_cielo" required>
                    <option value="" disabled selected>Selecciona una opción</option>
                    <option value="NUBOSO">NUBOSO</option>
                    <option value="DESPEJADO">DESPEJADO</option>
                    <option value="SOLEADO">SOLEADO</option>
                    <option value="NEVANDO">NEVANDO</option>
                </select>
            </div>

            <div class="form-group">
                <label for="visibilidad">Visibilidad</label>
                <input type="text" id="visibilidad" name="visibilidad" required placeholder="Ej. Buena, Regular, Mala...">
            </div>

            <div class="form-row">
                <div class="form-group">
                    <label for="temp_min">Temperatura Mínima</label>
                    <div class="input-with-unit">
                        <input type="text" id="temp_min" name="temp_min" required placeholder="Ej. -5">
                        <span class="unit">°C</span>
                    </div>
                </div>
                <div class="form-group">
                    <label for="temp_max">Temperatura Máxima</label>
                    <div class="input-with-unit">
                        <input type="text" id="temp_max" name="temp_max" required placeholder="Ej. 2">
                        <span class="unit">°C</span>
                    </div>
                </div>
            </div>

            <div class="form-row">
                <div class="form-group">
                    <label for="viento_min">Viento Mínimo</label>
                    <div class="input-with-unit">
                        <input type="text" id="viento_min" name="viento_min" required placeholder="Ej. 10">
                        <span class="unit">km/h</span>
                    </div>
                </div>
                <div class="form-group">
                    <label for="viento_max">Viento Máximo</label>
                    <div class="input-with-unit">
                        <input type="text" id="viento_max" name="viento_max" required placeholder="Ej. 45">
                        <span class="unit">km/h</span>
                    </div>
                </div>
            </div>

            <div class="form-row">
                <div class="form-group">
                    <label for="espesor_min">Espesor Mínimo</label>
                    <div class="input-with-unit">
                        <input type="text" id="espesor_min" name="espesor_min" required placeholder="Ej. 40">
                        <span class="unit">cm</span>
                    </div>
                </div>
                <div class="form-group">
                    <label for="espesor_max">Espesor Máximo</label>
                    <div class="input-with-unit">
                        <input type="text" id="espesor_max" name="espesor_max" required placeholder="Ej. 120">
                        <span class="unit">cm</span>
                    </div>
                </div>
            </div>

            <div class="form-row">
                <div class="form-group">
                    <label for="remontes_abiertos">Remontes Abiertos</label>
                    <input type="text" id="remontes_abiertos" name="remontes_abiertos" required placeholder="Ej. 14">
                </div>
                <div class="form-group">
                    <label for="km_esquiables">Km Esquiables</label>
                    <div class="input-with-unit">
                        <input type="text" id="km_esquiables" name="km_esquiables" required placeholder="Ej. 55.5">
                        <span class="unit">km</span>
                    </div>
                </div>
            </div>

            <div class="form-group">
                <label for="numero_pistas">Número de Pistas (Total)</label>
                <input type="text" id="numero_pistas" name="numero_pistas" required placeholder="Ej. 42">
            </div>

            <div class="form-group">
                <label>Zonas / Pistas Abiertas</label>
                <div class="checkbox-group">
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Veleta"> Veleta</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Laguna"> Laguna</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Borreguiles"> Borreguiles</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Pista del Mar"> Pista del Mar</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Cauchiles Parador"> Cauchiles Parador</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Loma Dílar"> Loma Dílar</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Rio Monachil"> Rio Monachil</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Parque Sulayr"> Parque Sulayr</label>
                    <label class="checkbox-label"><input type="checkbox" name="pistas_abiertas[]" value="Jardín Alpino"> Jardín Alpino</label>
                </div>
            </div>

            <button type="submit">Enviar Parte de Nieve</button>
        </form>
    </div>

</body>
</html>

