        // A) PRODUCTOS
        const prodKey = `${productName} - ${variantKey}`;

        if (!productStats[prodKey]) {
            productStats[prodKey] = {
                Producto: productName, 
                Variante: variantOriginal, // Guardamos la original para que quede bonita en el Excel
                Precio: listPrice,
                Ventas: 0, Unidades: 0, Beneficio: 0
            };
        }
        productStats[prodKey].Ventas += paidPrice;
        productStats[prodKey].Unidades += 1;
        productStats[prodKey].Beneficio += (paidPrice - cost);

        // B) MARCAS
