        for (const marca of LISTA_MARCAS) {
            // Si la marca es un número (ej. "686"), usamos Regex de coincidencia exacta
            if (!isNaN(marca)) {
                const regex = new RegExp(`\\b${marca}\\b`);
                if (regex.test(productName)) {
                    detectedBrand = marca;
                    break;
                }
            } else {
                // Si es texto normal
                if (productName.includes(marca)) {
                    detectedBrand = marca;
                    break;
                }
            }
        }

        // Parche manual para The North Face (buscando en mayúsculas)
        if (detectedBrand === "OTRAS" && (productName.includes("NORTH FACE") || productName.includes(" TNF "))) {
            detectedBrand = "THE NORTH FACE";
        }

        if (!brandStats[detectedBrand]) {
            brandStats[detectedBrand] = { Marca: detectedBrand, Ventas: 0, Unidades: 0 };
        }
        brandStats[detectedBrand].Ventas += paidPrice;
        brandStats[detectedBrand].Unidades += 1;
    }

