// El nodo Code de n8n espera que devolvamos un array de objetos.
const outputItems = [];

// Obtenemos el valor del contador del nodo anterior
// items[0].json.Contador → 2 en tu ejemplo
let numeroFila = items[0].json.Contador;
let ID_documento = items[0].json.ID_Documento;

// 1. Accedemos al cuerpo real del pedido de Shopify, que está anidado.
const orderBody = $node['Webhook'].json.body; 

// 2. Extraemos los artículos de línea (productos) y la moneda.
const lineItems = orderBody.line_items;
const orderCurrency = orderBody.currency;

// Iteramos sobre cada producto individual
for (const item of lineItems) {

  const originalPrice = parseFloat(item.price);

  let totalDiscountAmount = 0;
  for (const allocation of item.discount_allocations) {
    totalDiscountAmount += parseFloat(allocation.amount);
  }

  const finalPaidPrice = originalPrice - totalDiscountAmount;

  // Creamos un nuevo objeto y añadimos "numero_fila"
  outputItems.push({
    json: {
      ID_documento: ID_documento,
      numero_fila: numeroFila,   // ← Nuevo campo
      product_name: item.title,
      sku: item.sku,
      variant_title: item.variant_title,
      original_price: originalPrice.toFixed(2),
      total_discount_applied: totalDiscountAmount.toFixed(2),
      final_paid_price: finalPaidPrice.toFixed(2),
      currency: orderCurrency
    }
  });

  // ↑ Incrementamos para el siguiente item
  numeroFila++;
}

// Devolvemos el array con los nuevos objetos.
return outputItems;

// 1. Validaciones básicas
if (items.length < 2) {
    throw new Error("Se esperaban al menos 2 ítems de entrada.");
}

// Inicializamos variables
let orderData = null;
const productRows = []; // Aquí guardaremos los ítems que tienen ID_documento
const costMap = {}; // Aquí guardaremos los costos indexados por SKU

// 2. Clasificar los ítems de entrada
for (const item of items) {
    const json = item.json;

    // A) Es la ORDEN (tiene data.order)
    if (json.data && json.data.order) {
        orderData = json.data.order;
    } 
    // B) Es información de COSTO de Shopify (tiene data.productVariant)
    else if (json.data && json.data.productVariant) {
        const variant = json.data.productVariant;
        const sku = variant.sku;
        const costAmount = variant.inventoryItem?.unitCost?.amount;

        // Guardamos el costo en un objeto usando el SKU como clave para buscarlo rápido después
        if (sku && costAmount) {
            costMap[sku] = costAmount;
        }
    }
    // C) Es el ítem procesado (tiene ID_documento)
    else if (json.ID_documento) {
        productRows.push(json);
    }
}

if (!orderData) {
    throw new Error("No se encontró el ítem de la orden de Shopify.");
}

if (productRows.length === 0) {
    throw new Error("No se encontraron ítems de producto con ID_documento.");
}

// 3. Crear objeto base de la orden
const baseOrderData = {
    id: orderData.id,
    name: orderData.name,
    createdAt: orderData.createdAt,
    totalPrice: orderData.totalPriceSet?.shopMoney?.amount || 'N/A',
    currencyCode: orderData.totalPriceSet?.shopMoney?.currencyCode || 'N/A',

    // Datos del cliente
    customer: orderData.customer,

    // Datos de envío
    fulfillmentLocation: orderData.fulfillments?.[0]?.location?.name || 'N/A',
    fulfillmentAddress: orderData.fulfillments?.[0]?.location?.address || 'N/A',

    // Datos de la transacción
    transactionGateway: orderData.transactions?.[0]?.formattedGateway || 'N/A',
    transactionKind: orderData.transactions?.[0]?.kind || 'N/A',
    transactionStatus: orderData.transactions?.[0]?.status || 'N/A',
};

// 4. Iterar sobre los productos y fusionar (incluyendo el costo encontrado)
const combinedItems = [];

for (const productData of productRows) {

    // Buscamos si existe un costo guardado para este SKU
    const sku = productData.sku;
    const unitCost = costMap[sku] || '0.00'; // Si no encuentra match, pone 0.00 o N/A

    const combinedItem = {
        ...baseOrderData,

        product: {
            ID_documento: productData.ID_documento,
            numero_fila: productData.numero_fila,
            product_name: productData.product_name,
            sku: productData.sku,
            variant_title: productData.variant_title,
            original_price: productData.original_price,
            total_discount_applied: productData.total_discount_applied,
            final_paid_price: productData.final_paid_price,
            currency: productData.currency,
            // Aquí agregamos el campo solicitado
            unit_cost: unitCost 
        }
    };

    combinedItems.push({
        json: combinedItem,
    });
}

return combinedItems;

